# coding=utf-8from time import sleepfrom appium import webdriverfrom appium.webdriver.common.touch_action import TouchActiondesired_caps = {    'platformName': 'Android',    'platformVersion': '8.0.0',    'deviceName': 'Android Emulator',    'appPackage': 'com.example.notes',    'appActivity': '.Activity.MainActivity',    'unicodeKeyboard': True,  # 是使用unicode编码方式发送字符串    'resetKeyboard': True  # 隐藏键盘}# desired_caps['platformName'] = 'Android'# desired_caps['platformVersion'] = '8.0.0'# desired_caps['deviceName'] = 'Android Emulator'# desired_caps['appPackage'] = 'com.evernote'# desired_caps['appActivity'] = '.ui.HomeActivity'desired_caps['noReset'] = True #检查如果程序已经安装就不会再重复安装，直接启动driver = webdriver.Remote('http://localhost:4723/wd/hub', desired_caps)#等待加载完成sleep(4)driver.find_element_by_id("com.example.notes:id/fab_expand_menu_button").click()sleep(0.5)#快速创建driver.find_element_by_id("com.example.notes:id/action_quick").click()sleep(0.5)driver.find_element_by_id("com.example.notes:id/content_quick").send_keys(u'你好，这是我的第一篇快速创建的备忘录')# driver.find_element_by_id("com.example.notes:id/location_bottom_quick").click()driver.find_element_by_xpath("//android.widget.TextView[@content-desc='完成']").click()#driver.find_element_by_id("com.example.notes:id/fab_expand_menu_button").click()#新建备忘录driver.find_element_by_id("com.example.notes:id/action_note").click()driver.find_element_by_id("com.example.notes:id/title_create").send_keys(u'hello world')driver.find_element_by_id("editor").send_keys('这是我第二篇创建的备忘录')driver.find_element_by_xpath("//android.widget.TextView[@content-desc='完成']").click()sleep(2)driver.quit()